import { useState, useEffect, useCallback } from "react"
import './index.css'
export default function ReactCalender(props) {
    const { value, onChange, style = {}, className = "", cancalTxt = 'cancel', selectTxt = 'select' } = props
    //const levelOneColorList = ['#ff0000', '#ffff00', '#00ff00', '#00ffff', '#0000ff', '#ff00ff']
    const [showPicker, setShowPicker] = useState(false)
    const [color, setColor] = useState('#ff0000')
    const [levelOneLeft, setLevelOneLeft] = useState(0)
    const cancel = useCallback(() => {
        setShowPicker(false)
    }, [])

    const select = useCallback(() => {
        onChange(color)

    }, [])

    const clickLevelOne = useCallback((e) => {
        const ratio = e.clientX / window.screen.width * 100
        let result = ``
        let flag = e.clientX / window.screen.width * 5
        if (ratio >= 0 && ratio <= 20) {
            result = `rgb(255,${0xff * flag},0)`
        } else if (ratio >= 20 && ratio < 40) {
            result = `rgb(${0xff - 0xff * (flag - 1)},255,0)`
        } else if (ratio >= 40 && ratio < 60) {
            result = `rgb(0,255,${0xff * (flag - 2)})`
        } else if (ratio >= 60 && ratio < 80) {
            result = `rgb(0,${0xff - 0xff * (flag - 3)},255)`
        } else if (ratio >= 80 && ratio <= 100) {
            result = `rgb(${0xff * (flag - 4)},0,255)`
        }

        setColor(result)//calculate color value
        setLevelOneLeft(ratio)
    }, [])

    return (
        <>
            <input className={className} type="text" value={value} style={style} onClick={() => setShowPicker(true)} />
            <div className="react-color-picker-content" style={!showPicker ? { display: "none" } : {}}>
                <div className="react-color-picker-header">
                    <div className="react-color-picker-header-cancel" onClick={() => cancel()}>{cancalTxt}</div>
                    <div className="react-color-picker-header-select" onClick={() => select()}>{selectTxt}</div>
                </div>
                <div className="react-picker-body">
                    <div className="react-color-list-level-one" onTouchMove={(e) => clickLevelOne(e)}>
                        <div
                            className="react-color-list-level-one-slider"
                            style={{ left: `${levelOneLeft}%`, backgroundColor: color }}
                        ></div>
                    </div>
                    <div className="react-color-list-level-two">

                    </div>
                </div>
            </div>
        </>
    )
}
