    const [showPicker, setShowPicker] = useState(false)
    const cancel = useCallback(() => {
        console.log(111)
    },[])
    return (
        <>
            <input className={className} type="text" value={value} style={style} onClick={() => setShowPicker(true)} />
            <div className="pickerBox" style={!showPicker ? { display: "none" } : {}}>
                <div className="pickerHeader">
                    <div onClick={()=>cancel() }>{cancalTxt}</div>
                    <div>{selectTxt}</div>
                </div>
                <div className="pickerBody"></div>
            </div>
        </>
    )
}
